sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	//============Variables From Credit Application Form=============
	var number = g_form.getValue('number');
	var date = g_form.getValue('date');

	var firmName = g_form.getValue('firm_name');

	var companyName = g_form.getValue("u_reference_company");
	var phone = g_form.getValue("u_reference_phone_1");
	var email = g_form.getValue("u_ref_email_1");
	var address = g_form.getValue("u_reference_address_1");
	var state = g_form.getValue("u_ref_state_1");
	var city = g_form.getValue("u_ref_city_1");
	var otherCity = g_form.getValue("u_ref_other_city_1");
	var zip = g_form.getValue("u_ref_zip");
	//===============================================================

   //Type appropriate comment here, and begin script below
	if(newValue == "Yes"){
		g_form.clearMessages();	
		checkTradeEmptyField(number,date,firmName,companyName,phone,email,address,state,city,otherCity,zip);
	}
	else if(newValue == "Clear"){
		g_form.clearMessages();
		clearTradeFields();

	}

	g_form.clearValue("u_trade_ref_save");   
}

function checkTradeEmptyField(number,date,firmName,companyName,phone,email,address,state,city,otherCity,zip){
	var message = "";
	var empty;

	if(companyName === "" || phone === "" || email === "" || address === "" || state === "" || city === "" || zip === ""){
		empty = true;
	}
	else{
		empty = false;
	}


	if(empty === true){
		message += "<font color='red'>Please Fill Empty Fields: </font>";

		if(companyName === ""){
			message += "</br>Company Name";
			g_form.flash("u_reference_company","red",0);
		}
		if(phone === ""){
			message += "</br>Phone";
			g_form.flash("u_reference_phone_1","red",0);
		}
		if(email === ""){
			message += "</br>Email";
			g_form.flash("u_ref_email_1","red",0);
		}
		if(address === ""){
			message += "</br>Address";
			g_form.flash("u_reference_address_1","red",0);
		}
		if(state === ""){
			message += "</br>State";
			g_form.flash("u_ref_state_1","red",0);
		}
		if(city === ""){
			message += "</br>City";
			g_form.flash("u_ref_city_1","red",0);
		}
		if(zip === ""){
			message += "</br>Zip";
			g_form.flash("u_ref_zip","red",0);
		}

	}else{

		var continueOn = confirm("Are You Sure You Want To Save ["+ companyName +"] as ["+ firmName +"] Trade Reference?");

		if(continueOn){

			saveReference(number,date,companyName,phone,email,address,state,city,otherCity,zip);

			message = "<font color='green'>This Trade Reference Has Been Saved.</font>" +
				  "</br>Company Name: " + companyName +
				  "</br>Phone: " + phone +
				  "</br>Email: " + email +
				  "</br>Address: " + address +
				  "</br>State: " + state +
				  "</br>City: " + city;

			if(city === "Other"){
				message += "</br>Other City:" + otherCity +
						   "</br>Zip: " + zip;
			}else{
				message += "</br>Zip: " + zip;
			}

		}
		else{
			message = "<font color='yellow'>Trade Reference Was Not Saved.</font>";
		}
	}	

	g_form.addInfoMessage(message);

	if(continueOn){
		clearFields();		
	}
}

function clearTradeFields(){

	var continueOn = confirm("Are You Sure You Want To Clear The Trade Reference Fields?");

	if(continueOn){
		clearFields();
	}	
}

function clearFields(){
	g_form.clearValue("u_reference_company");
	g_form.clearValue("u_reference_phone_1");
	g_form.clearValue("u_ref_email_1");
	g_form.clearValue("u_reference_address_1");
	g_form.clearValue("u_ref_state_1");
	g_form.clearValue("u_ref_city_1");
	g_form.clearValue("u_ref_other_city_1");
	g_form.clearValue("u_ref_zip");
	g_form.addInfoMessage("<font color='red'>All Trade Reference Fields Have Been Cleared.</font>");
}

function saveReference(number,date,companyName,phone,email,address,state,city,otherCity,zip){

	var tradeRef = new GlideAjax('GlideAjaxAddReference');
	tradeRef.addParam('sysparm_name','addTradeReference');

	tradeRef.addParam('sysparm_number', number);
	tradeRef.addParam('sysparm_date', date);
	tradeRef.addParam('sysparm_company_name',companyName);
	tradeRef.addParam('sysparm_phone', phone);
	tradeRef.addParam('sysparm_email', email);
	tradeRef.addParam('sysparm_address', address);
	tradeRef.addParam('sysparm_state', state);
	tradeRef.addParam('sysparm_city', city);
	tradeRef.addParam('sysparm_other_city', otherCity);
	tradeRef.addParam('sysparm_zip', zip);

	tradeRef.getXML(callBackFunction);

	function callBackFunction(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
	}
}
